class CatalogItem extends StatelessWidget {
  final String productName;
  final String sellerName;
  final String quantityInKgs;
  final String distanceInKms;
  final String imageLink;
  final String itemId;
  final String prizePerKg;
  const CatalogItem({
    Key? key,
    required this.productName,
    required this.sellerName,
    required this.quantityInKgs,
    required this.distanceInKms,
    required this.imageLink,
    required this.itemId,
    required this.prizePerKg,
  }) : super(key: key);
  @override
  Widget build(BuildContext context) {
    return Container(
      margin: const EdgeInsets.all(10),
      padding: const EdgeInsets.all(10),
      decoration: BoxDecoration(
          color: Colors.grey[200],
          borderRadius: const BorderRadius.all(Radius.circular(10))),
      child: Row(
        mainAxisAlignment: MainAxisAlignment.spaceEvenly,
        children: [
          ClipRRect(
            borderRadius: const BorderRadius.all(Radius.circular(20)),
            child: Image.network(
              imageLink,
              height: 125,
              width: 125,
            ),
          ),
          const SizedBox(width: 20),
          Column(
            crossAxisAlignment: CrossAxisAlignment.start,
            children: [
              Text(
                productName,
                style: const TextStyle(
                  fontSize: 22,
                  fontWeight: FontWeight.bold,
                ),
              ),
              Text(
                sellerName,
                style: const TextStyle(
                  fontSize: 15,
                ),
              ),
              Text(
                "$quantityInKgs Kgs",
                style: const TextStyle(
                  fontSize: 15,
                ),
              ),
              Text(
                "$distanceInKms Kms",
                style: const TextStyle(
                  fontSize: 15,
                ),
              ),
              Text(
                "$prizePerKg per Kg",
                style: const TextStyle(
                  fontSize: 15,
                ),
              ),
            ],
          ),
          const SizedBox(width: 20),
          ElevatedButton(
            onPressed: () {
              if (FirebaseAuth.instance.currentUser == null) {
                Navigator.of(context).pushReplacement(MaterialPageRoute(
                    builder: (context) => const LoginScreen()));
              } else {
                Navigator.push(
                  context,
                  MaterialPageRoute(
                      builder: (context) => BuyPage(
                            productName: productName,
                            sellerName: sellerName,
                            quantityInKgs: quantityInKgs,
                            distanceInKms: distanceInKms,
                            imageLink: imageLink,
                            itemId: itemId,
                            prizePerKg: prizePerKg,
                          )),
                );
              }
            },
            style: ButtonStyle(
                backgroundColor: MaterialStateProperty.all<Color>(Colors.blue)),
            child: const Text("Buy"),
          ),
        ],
      ),
    );
  }
}

